{% set now_datetime = frappe.utils.now_datetime() %}
{% set from_time = now_datetime.strftime('%Y-%m-%d 00:00:00') %}
{% set to_time = now_datetime.strftime('%Y-%m-%d %H:%m:%S') %}
{% set employees = frappe.get_all('Employee', filters={'default_shift': doc.name}, fields=['employee_name']) %}
{% set checkins = frappe.get_all('Employee Checkin', filters={'log_type': 'IN', 'shift': doc.name, 'time': ('>=', from_time), 'time': ('<=', to_time)}, fields=['name', 'employee_name', 'time'], group_by='employee_name') %}
{% set leaves_employees = frappe.get_all('Attendance', filters={'attendance_date': frappe.utils.nowdate() ,'status': 'On Leave'}, fields=['name'] ) %}
<h2>Dashboard</h2>

<style>
table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}

tr:nth-child(even) {
  background-color: #dddddd;
}
</style>
<table>
  <tr>
    <th style="text-align: center">Shift Name</th>
    <th style="text-align: center">Shift Employees</th>
    <th style="text-align: center">Present</th>
    <th style="text-align: center">Absent</th>
    <th style="text-align: center">On Leave</th>
  </tr>
  <tr>
    <td style="text-align: center">{{ doc.name }}</td>
    <td style="text-align: center">{{ employees|length }}</td>
    <td style="text-align: center">{{ checkins|length }}</td>
    <td style="text-align: center">{{ (employees|length - checkins|length )-leaves_employees|length }}</td>
    <td style="text-align: center">{{ leaves_employees|length }}</td>
  </tr>
</table>

<div class="col-md-4">
<ol>
{% for i in employees %}
<li>
{{ i.employee_name }}
</li>
{% endfor %}
</ol>
</div>

<div class="col-md-4">
<ol>
{% for j in checkins %}
<li>
{{ j.employee_name }}
</li>
{% endfor %}
</ol>
</div>
